// Loop over input items and add a new field called 'myNewField' to the JSON of each one
let result = false;
if ($input?.first()?.json?._id) {
  result = true;
}

return {
  result: result,
  propertyName: $('On form submission').first().json['Project Name']
}

// Get all items from the merge node
const allItems = $input.all();

// Initialize the result structure
const result = {
  core_property_unit_specifics: {
    project_overview: {},
    unit_specifics: {}
  },
  location_connectivity_amenities: {
    connectivity: {},
    on_site_facilities: {},
    surrounding_amenities: {},
    recreational_lifestyle: {},
    education_institutions: {} 
  },
  market_performance_analysis: {
    sales_transactions_pricing: {},
    rental_market_analysis: {},
    ownership_costs: {}
  }
};

// Process each item from the merge node
allItems.forEach(item => {
  // The various possible input shapes
  const apiResponse = item.json;

  // Case 1: OpenAI/Anthropic-style JSON
  if (apiResponse && apiResponse.choices && apiResponse.choices[0]) {
    const content = apiResponse.choices[0].message.content;
    const citations = apiResponse.citations || [];

    if (!content) return [];

    try {
      const parsedContent = JSON.parse(content);

      // Development Overview
      if (parsedContent.development_overview) {
        result.core_property_unit_specifics.project_overview = {
          data: {
            project_id: $input.first().json.objectId,
            project_type: $input.first().json.objectType,
            project_name: $input.first().json.displayText,
            project_display_type: $input.first().json.displayType,
            project_description: $input.first().json.displayDescription,
            property_type_group: $input.first().json.properties.propertyTypeGroup,
            district: $input.first().json.properties.district,
            ...parsedContent.development_overview
          },
          sources: citations
        };
      }

      // Unit Specifics
      if (parsedContent.unit_specifics) {
        console.log('unit specifics');
        result.core_property_unit_specifics.unit_specifics = {
          data: parsedContent.unit_specifics,
          sources: citations
        };
      }

      // Transport Connectivity
      if (parsedContent.connectivity) {
        result.location_connectivity_amenities.connectivity = {
          data: parsedContent.connectivity,
          sources: citations
        };
      }

      // On-Site Facilities
      if (parsedContent.on_site_facilities) {
        result.location_connectivity_amenities.on_site_facilities = {
          data: parsedContent.on_site_facilities,
          sources: citations
        };
      }

      // Surrounding Amenities
      if (parsedContent.surrounding_amenities) {
        result.location_connectivity_amenities.surrounding_amenities = {
          data: parsedContent.surrounding_amenities,
          sources: citations
        };
      }

      // Recreational Lifestyle
      if (parsedContent.recreational_lifestyle) {
result.location_connectivity_amenities.recreational_lifestyle = {
          data: parsedContent.recreational_lifestyle,
          sources: citations
        };
      }

      // Education Institution
      if (parsedContent.education_institutions) {
        result.location_connectivity_amenities.education_institutions = {
          data: parsedContent.education_institutions,
          sources: citations
        };
      }

      // Sales Transactions Pricing
      if (parsedContent.sales_transactions_pricing) {
        result.market_performance_analysis.sales_transactions_pricing = {
          data: parsedContent.sales_transactions_pricing,
          sources: citations
        };
      }

      // Rental Market Analysis
      if (parsedContent.rental_market_analysis) {
        result.market_performance_analysis.rental_market_analysis = {
          data: parsedContent.rental_market_analysis,
          sources: citations
        };
      }

      // Ownership Costs
      if (parsedContent.ownership_costs) {
        result.market_performance_analysis.ownership_costs = {
          data: parsedContent.ownership_costs,
          sources: citations
        };
      }

    } catch (error) {
      console.log('Failed to parse JSON for item:', error.message);
      console.log('Content was:', content);
    }

  // Case 2: Direct data shape (e.g., project scores directly at top level)
  } else if (apiResponse && (apiResponse.main_project || apiResponse.similar_projects)) {
    // Defensive: Only fill it if not already filled
    if (!result.comparison_similar_projects || Object.keys(result.comparison_similar_projects).length === 0) {
      result.comparison_similar_projects = {
        data: {
          main_project: apiResponse.main_project || undefined,
          similar_projects: apiResponse.similar_projects || undefined
        },
        sources: [] // Add citations if present at this level
      };
    }
  }
});

// Clean up empty sections
Object.keys(result).forEach(key => {
  if (Object.keys(result[key]).length === 0) {
    delete result[key];
  }
});

return result instanceof Array
  ? result.map(item => ({ json: {project: item} }))
  : [{ json: {project: result} }];

const input = $input.all();

return input.map((item, index) => {
  try {
    const rawOutput = item.json?.output;

    if (!rawOutput || typeof rawOutput !== 'string') {
      throw new Error(`Missing or invalid 'output' at index ${index}`);
    }

    const parsedOutput = JSON.parse(rawOutput);

    return {
      json: parsedOutput
    };
  } catch (error) {
    return {
      json: {
        error: error.message,
        originalOutput: item.json?.output
      }
    };
  }
});

const input = $input.all();

return input.map((item, index) => {
  try {
    const rawOutput = item.json?.output;

    if (!rawOutput || typeof rawOutput !== 'string') {
      throw new Error(`Missing or invalid 'output' at index ${index}`);
    }

    const parsedOutput = JSON.parse(rawOutput);

    return {
