});

const allItems = $input.all();

let propertyName = $input.first().json.displayText;
let unitTypes = [];

for (const item of allItems) {
  const json = item.json;

  console.log(json);

  // Handle OpenAI/Anthropic-style completions
  if (json.choices && Array.isArray(json.choices) && json.choices[0]?.message?.content) {
    try {
      const parsed = JSON.parse(json.choices[0].message.content);
      const types = parsed.unit_specifics?.unit_types || [];

      console.log('unit types', types);

      unitTypes = types.map(unit => {
        let type = unit.type.trim();

        // Normalize common separators to hyphens
        type = type
          .replace(/\s*\+\s*/g, '-')        // Replace + with -
          .replace(/\s*\/\s*/g, '-');       // Replace / with -

        // Insert hyphen before a capitalized word that immediately follows a number and 'BR'
        // This handles cases like "4BRPremium", "1BRStudy", "2BRFlex"
        type = type.replace(/(\dBR)([A-Z][a-z]*)/g, '$1-$2');

        // Remove any remaining spaces
        type = type.replace(/\s+/g, '');

        return {
          key: type
        };
      });

    } catch (err) {
      console.log("Failed to parse unit_types:", err.message);
    }
  }
}

// Return clean result
return [
  {
    json: {
      propertyName: propertyName,
      unitTypes: unitTypes
    }
  }
];

